import React, { useState, useEffect, useRef } from "react";
import { useAuth } from "../contexts/AuthProvider";
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  updateCurrentUser,
  onAuthStateChanged,
} from "firebase/auth";
import { getFirestore, setDoc, doc } from "firebase/firestore";

const Login = () => {
  const nameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const db = getFirestore();
  const { signup } = useAuth;
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false)

  const submitHandler = ( e ) => {
    e.preventDefault()

    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError('Passwords do not match')
    }
    try {
      setError('')
      setLoading(true)
      signup(emailRef.current.value, passwordRef.current.value);
    } catch {
      setError('Failed to create an account')
    }
    setLoading(false)
  }
    // onAuthStateChanged(auth, (user) => {
    //   if (user) {
    //     // once the user logs in, we need to add them to the database as a reference
    //     // query the users collection to find the user
    //     const useRef = doc(db, "users", user.uid);
    //     // if that user doesn't exist, add them to the database
    //     // otherwise, if the user does exist, overwrite (don't duplicate) their information
    //     setDoc(
    //       useRef,
    //       { email: user.email, name: user.displayName },
    //       { merge: true }
    //     );
    //     setCurrentUser({
    //       id: user.uid,
    //       name: user.displayName,
    //       image: user.photoURL,
    //       email: user.email,
    //       loggedIn: true,
    //     });
    //   }
    // });

  return (
    <div className="d-flex align-items-center justify-content-center">
      <div className="card col-10">
        <div className="card-body">
          <h2>Sign In</h2>
          <form onSubmit={submitHandler} id="login-form">
            <div className="form-group row">
              <label for="inputPassword" className="col-sm-2 col-form-label">
                Name
              </label>
              <div className="col-4">
                <input
                  type="text"
                  className="form-control"
                  name="username"
                  ref={nameRef}
                />
              </div>
            </div>
            <div className="form-group row">
              <label for="staticEmail" className="col-sm-2 col-form-label">
                Email
              </label>
              <div className="col-4">
                <input
                  type="email"
                  className="form-control"
                  name="userEmail"
                  ref={emailRef}
                  placeholder="email@example.com"
                />
              </div>
            </div>
            <div className="form-group row">
              <label for="inputPassword" className="col-sm-2 col-form-label">
                Password
              </label>
              <div className="col-4">
                <input
                  type="password"
                  className="form-control"
                  name="userPassword"
                  ref={passwordRef}
                />
              </div>
            </div>
            <div className="form-group row">
              <label for="inputPassword" className="col-sm-2 col-form-label">
                Confirm Password
              </label>
              <div className="col-4">
                <input
                  type="password"
                  className="form-control"
                  name="userPassword"
                  ref={passwordConfirmRef}
                />
              </div>
            </div>
            <button
              id="login-submit"
              type="submit"
              className="btn btn-outline-muted"
              disabled
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
