import React, { useRef, useState, Fragment } from "react";
import moment from 'moment';
import { useData } from "../contexts/DataProvider";
import { useAuth } from "../contexts/AuthProvider";
import {} from 'react-bootstrap';

const Sent = () => {
  const { sent, userEmail } = useData();
  const { currentUser } = useAuth();
  const messageRef = useRef();
  const userRef = useRef();
  const [filteredSent, setFilteredSent] = useState([]);

  // Create function to handle form submission
  const submitHandler = ( e ) => {
    e.preventDefault();
    const filteredSentList = [];

    // Loop through sent messages list
    for(const s of sent){
      if (s.body.includes(userEmail)) {
        filteredSentList.push(s);
      }
    }
    setFilteredSent(filteredSentList)
    messageRef.current.value = '';
    userRef.current.value = '';
  };
  return (
    <Fragment>
      <div className="container sent">
        <h1 className="text-center">Sent</h1>
          <form className="container form col-6" onSubmit={submitHandler}>
            <ul>
              <li className="list-group-item">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Message To"
                  ref={userRef}
                />
              </li>
              <li className="list-group-item mt-1">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Message"
                  ref={messageRef}
                />
              </li>
            </ul>
            <div>
            <button type="submit" className="btn float-right" id="send-btn">
              Send
            </button>
            </div>
          </form>
          </div>
          <div className="container sent">
            {currentUser.loggedIn && filteredSent.length < 1
              ? sent.map((s) => (
                  <li key={s.id} className="list-group-item">
                    <div>
                      {s?.message}
                      <span className="float-right">
                        <small>{moment(s.TimeSent?.toDate()).fromNow()}</small>
                      </span>
                    </div>
                    <div>
                      <cite> &mdash; {`${s.user?.email}`} </cite>
                    </div>
                  </li>
                ))
              : filteredSent.map((s) => (
                  <li key={s.id} className="list-group-item">
                    <div>
                      {s?.message}
                      <span className="float-right">
                        <small>{moment(s.timeSent?.toDate()).fromNow()}</small>
                      </span>
                    </div>
                    <div>
                      <cite> &mdash; {`${s.user?.email}`} </cite>
                    </div>
                  </li>
                ))}
          </div>
    </Fragment>
  );
};
export default Sent;
