import React, { useRef } from "react";
import { useData } from "../contexts/DataProvider";
import { useAuth } from "../contexts/AuthProvider";

const Sent = () => {
  const { sent } = useData();
  const { currentUser } = useAuth();
  const messageRef = useRef();
  const userRef = useRef();
  const [filteredSent, setFilteredSent] = useState([]);
  // Create function to 
  const submitHandler = ( e ) => {
    e.preventDefault();
    messageRef.current.value = '';
    userRef.current.value = '';
  };
  return (
    <div className="container about">
      <h1 className="text-center">Sent</h1>
      <div>
        <form className="container form col-6" onSubmit={submitHandler}>
          <ul>
            <li className="list-group-item">
              <input
                type="text"
                className="form-control"
                placeholder="Message To"
                ref={userRef}
              />
            </li>
            <li className="list-group-item mt-1">
              <input
                type="text"
                className="form-control"
                placeholder="Message"
                ref={messageRef}
              />
            </li>
          </ul>
          <button type="submit" className="btn float-right" id="send-btn">
            Send
          </button>
        </form>
        <div className="sent">
          {currentUser.loggedIn && filteredMessages.length < 1
            ? messages.map((m) => (
                <li key={m.id} className="list-group-item">
                  <div>
                    {m?.body}
                    <span className="float-right">
                      <small>{moment(m.dateCreated?.toDate()).fromNow()}</small>
                    </span>
                  </div>
                  <div>
                    <cite> &mdash; {`${m.user?.name}`} </cite>
                  </div>
                </li>
              )): null}
        </div>
      </div>
    </div>
  );
};
export default Sent;
