import React, { Fragment, useState, useEffect } from "react";

const Posts = () => {
    this.state = {
        posts: [],
    };
  useEffect(() => {
    console.log("mounted");
    axios
      .get("https://fakebook-january-derek.herokuapp.com/api/v1/blog")
      .then((res) =>
        this.setState({
          posts: res.data,
        })
      );
  });
  return (
    <Fragment>
      <div className="row">
        <div className="col-12">
          <ul className="list-group">
            {this.state.posts.map((p) => (
              <li key={p.id} className="list-group-item">
                <div>
                  <Link to={`/blog/${p.id}`}>{p.body}</Link>
                  <span className="float-right">
                    <small>{moment(p.date_created).fromNow()}</small>
                  </span>
                </div>
                <div>
                  <cite>
                    {" "}
                    &mdash; {`${p.user_id.first_name} ${p.user_id.last_name}`}{" "}
                  </cite>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </Fragment>
  );
};
export default Posts;
