import React, { Fragment, useState, useEffect } from "react";
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  updateCurrentUser,
  onAuthStateChanged,
} from "firebase/auth";
import { getFirestore, setDoc, doc } from "firebase/firestore";

const Login = () => {

  const[enteredEmail, setEnteredEmail] = useState('');
  const[enteredPassword, setEnteredPassword] = useState('');
  const db = getFirestore();
  const [currentUser, setCurrentUser] = useState({ loggedIn: false });
  const emailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const passwordChangeHandler = event => {
    setEnteredPassword(event.target.value);
  };
  const loginSubmitHandler = (event) => {
    event.preventDefault();
  };

  const auth = getAuth();
  createUserWithEmailAndPassword(auth, enteredEmail, enteredPassword)
    .then((userCredential) => {
      // Signed in
      const user = userCredential.user;
      console.log("Account created!")
      // ...
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      console.log(errorCode, errorMessage);
    });
    signInWithEmailAndPassword(auth, enteredEmail, enteredPassword)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        // ...
      })
      .catch((error) => {
        const errorCode = error.code;
        const errorMessage = error.message;
      });
      useEffect(() => {
        onAuthStateChanged(auth, (user) => {
          if (user) {
            // once the user logs in, we need to add them to the database as a reference
            // query the users collection to find the user
            const useRef = doc(db, "users", user.uid);
            // if that user doesn't exist, add them to the database
            // otherwise, if the user does exist, overwrite (don't duplicate) their information
            setDoc(
              useRef,
              { email: user.email, name: user.displayName },
              { merge: true }
            );
            setCurrentUser({
              id: user.uid,
              name: user.displayName,
              image: user.photoURL,
              email: user.email,
              loggedIn: true,
            });
          }
        });
      }, [auth, db]);

  return (
    <Fragment>
      <div className="container">
        <div>
          <form id="login-form" onSubmit={loginSubmitHandler}>
            <div className="form-group row">
              <label for="staticEmail" className="col-sm-2 col-form-label">
                Email
              </label>
              <div className="col-sm-10">
                <input
                  type="email"
                  className="form-control"
                  name="userEmail"
                  placeholder="email@example.com"
                  onChange={emailChangeHandler}
                />
              </div>
            </div>
            <div className="form-group row">
              <label for="inputPassword" className="col-sm-2 col-form-label">
                Password
              </label>
              <div className="col-sm-10">
                <input
                  type="password"
                  className="form-control"
                  name="userPassword"
                  onChange={passwordChangeHandler}
                />
              </div>
            </div>

            <button
              id="login-submit"
              type="submit"
              className="btn float-right btn-muted"
              onSubmit={createUserWithEmailAndPassword}
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default Login;
