import React, { useRef, Fragment } from "react";
import moment from "moment";
import { useData } from "../contexts/DataProvider";
import { useAuth } from "../contexts/AuthProvider";
import { Card } from "react-bootstrap";

const Sent = () => {
  const { sent, userEmail } = useData();
  const { currentUser } = useAuth();
  const messageRef = useRef();
  const userRef = useRef();

  // Create function to handle message sending
  const sendMessageHandler = (e) => {
    e.preventDefault();

    const addPost = async (formData) => {
      let collectionRef = await collection(db, `users/${currentUser.id}/posts`);
      const docRef = await addDoc(collectionRef, formData);
      const newDoc = await getDoc(docRef);
      const userRef = await getDoc(docRef.parent.parent);
      setPosts([
        { id: newDoc.id, ...newDoc.data(), user: { ...userRef.data() } },
        ...posts,
      ]);
    };

    messageRef.current.value = "";
    userRef.current.value = "";
  };
  return (
    <Fragment>
      <div className="container sent">
        <h1 className="text-center">Sent</h1>
        <form className="container form col-6" onSubmit={sendMessageHandler}>
          <ul>
            <li className="list-group-item">
              <input
                type="text"
                className="form-control"
                placeholder="Message To"
                ref={userRef}
              />
            </li>
            <li className="list-group-item mt-1">
              <input
                type="text"
                className="form-control"
                placeholder="Message"
                ref={messageRef}
              />
            </li>
          </ul>
          <div>
            <button
              type="submit"
              className="btn float-right mb-4"
              id="send-btn"
            >
              Send
            </button>
          </div>
        </form>
      </div>
      <Card className="container justify-content-center bg-dark">
        <div className="container sent">
          {currentUser.loggedIn &&
            sent.map((s) => (
              <li key={s.id} className="list-group-item m-1">
                <div>
                  {s?.message}
                  <span className="float-right">
                    <small>{moment(s.timeSent?.toDate()).fromNow()}</small>
                  </span>
                </div>
                <div>
                  <cite> &#62; {`${s?.to}`} </cite>
                </div>
              </li>
            ))}
        </div>
      </Card>
    </Fragment>
  );
};
export default Sent;
