import React, { Fragment, useRef } from "react";
import "../index";
import Inbox from "../components/Inbox";
import { useData } from "../contexts/DataProvider";
import moment from "moment";
import { useAuth } from "../contexts/AuthProvider";
import {
  collectionGroup,
  getDoc,
  getDocs,
  query,
  getFirestore,
} from "firebase/firestore";

const Home = () => {
  const { messages } = useData();
  const { currentUser } = useAuth();
  const inputRef = useRef();
  const db = getFirestore();
  // console.log(messages)
  const findMessage = ( e ) => {
    e.preventDefault();
    console.log(inputRef.current.value)
    const getMessages = useCallback(async () => {
      // console.log(db)

      const q = query(collectionGroup(db, "messages"));

      let newMessages = [];

      const querySnapshot = await getDocs(q);

      querySnapshot.forEach(async (doc) => {
        const userRef = await getDoc(doc.ref.parent.parent);
        // console.log(userRef.data());

        newMessages.push({
          id: doc.id,
          ...doc.data(),
          user: { ...userRef.data() },
        });
        setMessages(newMessages);
        // console.log(doc.data())
      });
      return querySnapshot;
    }, [db]);
    inputRef.current.value = '';
  };
  return (
    <Fragment>
      <div className="container home">
        <h1>Home</h1>
        <form onSubmit={findMessage} className="input-group mb-5">
          <input
            type="text"
            className="form-control"
            placeholder="Search"
            ref={inputRef}
          />
          <div className="input-group-append">
            <button type="submit" className="btn btn-block" id="search-btn">
              Search
            </button>
          </div>
        </form>

        <Inbox />
        <div className="messages">
          {currentUser.loggedIn
            ? messages.map((m) => (
                <li key={m.id} className="list-group-item">
                  <div>
                    {m?.body}
                    <span className="float-right">
                      <small>{moment(m.dateCreated?.toDate()).fromNow()}</small>
                    </span>
                  </div>
                  <div>
                    <cite> &mdash; {`${m.user?.name}`} </cite>
                  </div>
                </li>
              ))
            : null}
        </div>
      </div>
    </Fragment>
  );
};
export default Home;
